using System; class Program { static void Main() { Console.WriteLine("Environment Variable Test"); Console.WriteLine("========================"); var openRouterKey = Environment.GetEnvironmentVariable("OPENROUTER_API_KEY"); Console.WriteLine($"OPENROUTER_API_KEY: {openRouterKey ?? "NOT SET"}"); if (!string.IsNullOrEmpty(openRouterKey)) { Console.WriteLine($"Key length: {openRouterKey.Length}"); if (openRouterKey.Length  { Console.WriteLine($"Key preview: {openRouterKey.Substring(0, 5)}...{openRouterKey.Substring(openRouterKey.Length - 5)}"); } } Console.WriteLine("\nAll environment variables:"); foreach (System.Collections.DictionaryEntry env in Environment.GetEnvironmentVariables()) { if (env.Key.ToString().Contains("OPENROUTER", StringComparison.OrdinalIgnoreCase)) { Console.WriteLine($"{env.Key}: {env.Value}"); } } } }  
